import numpy

from itertools import cycle

base_pattern = [0, 1, 0, -1]

def get_new_pattern(times_to_repeat, num_len) -> int:
    i = 0
    while True:
        for p in base_pattern:
            for _ in range(times_to_repeat):
                if i == 0:
                    i += 1
                    continue
                yield int(p)
                if i == num_len:
                    return
                i += 1

def get_pattern_matrix(num_len):
    return numpy.array([list(get_new_pattern(i, num_len)) for i in range(1, num_len + 1)])


def fft(input: str, phases: int) -> str:
    num_len = len(input)
    pattern_matrix = get_pattern_matrix(num_len)

    for _ in range(phases):
        output = ""
        product = numpy.matmul(pattern_matrix, numpy.array([int(ch) for ch in input]))
        output = "".join((str(num)[-1] for num in product))
        input = output
    return output


# help from https://old.reddit.com/r/adventofcode/comments/ebai4g/2019_day_16_solutions/fb58mb8/
def part2(input: str, phases=100) -> str:
    nums = [int(n) for n in input] * 10000
    offset = int(input[:7])
    data = nums[offset:]
    for _ in range(phases):
        for i in range(len(data) -1, 0, -1):
            data[i-1] = (data[i-1] + data[i]) % 10
    return ''.join(str(c) for c in data[:8])


