from collections import deque


def find_marker(stream: str, length: int) -> int:
    last_four = deque(maxlen=length)
    for i, ch in enumerate(stream):
        last_four.append(ch)
        if len(last_four) == length and len(set(last_four)) == length:
            return i + 1


if __name__ == '__main__':
    print(find_marker("", 14))