from collections import deque


def find_marker(stream: str) -> int:
    last_four = deque(maxlen=4)
    for i, ch in enumerate(stream):
        last_four.append(ch)
        if len(last_four) == 4 and len(set(last_four)) == 4:
            return i + 1


if __name__ == '__main__':
    print(find_marker(""))